mapper.select:
select count(*)
from EVALUATION
where 	work_idx=#{idx}
and		valuer=#{valuer}

mapper.insert:
INSERT INTO EVALUATION ( 
	   work_idx,
	   totalscore,
	   valuer
) VALUES (
	   #{idx},
	   #{totalScore},
	   #{valuer}
)

mapper.update:
update EVALUATION
set	  totalscore=#{totalScore}
where  work_idx=#{idx}
and	   valuer=#{valuer}
